import domain.DifficultyCategory
import domain.PuzzleDefinition

/**
 * Embedded puzzle library for web
 */
object PuzzleLibrary {
    
    val puzzles: Map<DifficultyCategory, List<PuzzleDefinition>> by lazy {
        mapOf(
            DifficultyCategory.EASY to easyPuzzles,
            DifficultyCategory.MEDIUM to mediumPuzzles,
            DifficultyCategory.HARD to hardPuzzles,
            DifficultyCategory.DIABOLICAL to diabolicalPuzzles
        )
    }
    
    fun getPuzzle(category: DifficultyCategory, index: Int): PuzzleDefinition? {
        return puzzles[category]?.getOrNull(index)
    }
    
    fun getRandomPuzzle(category: DifficultyCategory): PuzzleDefinition? {
        val list = puzzles[category] ?: return null
        return list.randomOrNull()
    }
    
    fun getAllPuzzles(): List<PuzzleDefinition> {
        return puzzles.values.flatten()
    }
    
